import React, { useState, useRef } from "react";
import {
  StyleSheet,
  View,
  Image,
  TouchableOpacity,
  Animated,
  Dimensions,
  Text,
  FlatList,
  useWindowDimensions,
} from "react-native";

const WIDTH = Dimensions.get("window").width;

const GettingStartedScreen = ({ navigation }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const scrollX = useRef(new Animated.Value(0)).current;
  const viewConfig = useRef({ viewAreaCoveragePercentThreshold: 50 }).current;
  const slidesRef = useRef(null);
  const viewableItemChanged = useRef(({ viewableItems }) => {
    setCurrentIndex(viewableItems[0].index);
  }).current;
  // useEffect(() => {
  //   console.log(`isLogkjbkhbhkkbgedIn`, isLoggedIn);
  //   setTimeout(() => {
  //     if (isLoggedIn) {
  //       navigation.navigate("HomeDrawer");
  //     }
  //   }, 100);
  // }, []);

  const { width, height } = useWindowDimensions();
  const slides = [
    {
      key: "1",
      image:
        "https://firebasestorage.googleapis.com/v0/b/touronapp-248e4.appspot.com/o/Touron%20app%2FIntros%2F1.jpg?alt=media&token=7fdeff79-3d51-40be-a7c9-514518df01e6",
    },
    {
      key: "2",
      image:
        "https://firebasestorage.googleapis.com/v0/b/touronapp-248e4.appspot.com/o/Touron%20app%2FIntros%2F2.jpg?alt=media&token=4b104e55-233d-4876-a0cc-6ad89c962112",
    },
    {
      key: "3",
      image:
        "https://firebasestorage.googleapis.com/v0/b/touronapp-248e4.appspot.com/o/Touron%20app%2FIntros%2F3.jpg?alt=media&token=64474172-eba3-40f9-a4b3-494d4621b8a7",
    },
    {
      key: "4",
      image:
        "https://firebasestorage.googleapis.com/v0/b/touronapp-248e4.appspot.com/o/Touron%20app%2FIntros%2F4.jpg?alt=media&token=54b2cf06-0f67-4583-878a-8c93c21c3ed6",
    },
  ];

  const _onDone = () => {
    navigation.navigate("HomeDrawer");
  };

  return (
    <>
      <View
        style={{
          justifyContent: "center",
          alignItems: "center",
          flex: 1,
          backgroundColor: "#fff",
        }}
      >
        <FlatList
          data={slides}
          key={(item, index) => item.key}
          renderItem={({ item }) => (
            <View>
              <Image
                source={{ uri: item.image }}
                resizeMode="cover"
                style={{ height: height, width: width }}
              />
            </View>
          )}
          horizontal
          showsHorizontalScrollIndicator={false}
          pagingEnabled
          scrollEventThrottle={32}
          onScroll={
            new Animated.event(
              [{ nativeEvent: { contentOffset: { x: scrollX } } }],
              { useNativeDriver: false }
            )
          }
          bounces={true}
          onViewableItemsChanged={viewableItemChanged}
          viewabilityConfig={viewConfig}
          ref={slidesRef}
        />
        {currentIndex === slides.length - 1 && (
          <TouchableOpacity onPress={_onDone}>
            <View
              style={{
                borderRadius: 5,
                paddingHorizontal: 20,
                paddingVertical: 6,
                backgroundColor: "#E28633",
              }}
            >
              <Text
                style={{ color: "#fff", fontFamily: "Andika", fontSize: 18 }}
              >
                Get Started
              </Text>
            </View>
          </TouchableOpacity>
        )}
        <View style={{ marginTop: 20, flexDirection: "row", height: 64 }}>
          {slides.map((s, i) => {
            const inputRange = [(i - 1) * width, i * width, (i + 1) * width];
            const dotWidth = scrollX.interpolate({
              inputRange,
              outputRange: [10, 20, 10],
              extrapolate: "clamp",
            });
            const opacity = scrollX.interpolate({
              inputRange,
              outputRange: [0.3, 1, 0.3],
              extrapolate: "clamp",
            });

            return (
              <Animated.View
                style={[styles.dot, { width: dotWidth, opacity }]}
                key={i.toString()}
              ></Animated.View>
            );
          })}
        </View>
      </View>
    </>
  );
};

export default GettingStartedScreen;

const styles = new StyleSheet.create({
  dot: {
    height: 10,
    borderRadius: 5,
    backgroundColor: "#E28633",
    marginHorizontal: 8,
  },
  loginButton: {
    marginVertical: 10,
    backgroundColor: "#fff",
    paddingHorizontal: 55,
    paddingVertical: 15,
    borderRadius: 10,
  },
  signupButton: {
    marginVertical: 20,
    backgroundColor: "black",
    paddingHorizontal: 45,
    paddingVertical: 15,
    borderRadius: 10,
  },
  buttonContainer: {
    position: "absolute",
    bottom: 0,
    width: WIDTH,
    alignItems: "center",
  },
});
