  //       // firebase
  //       storage()
  //         .ref(`users/${user.uid}/profile.jpg`)
  //         .put(blob)
  //         .then(() => {
  //           // firebase
  //           storage()
  //             .ref(`users/${user.uid}/profile.jpg`)
  //             .getDownloadURL()
  //             .then((imageUrl) => {
  //               setPic(imageUrl);
  //               updateProfilePic(imageUrl);
  //               console.log(imageUrl, "lo");
  //             });
  //         })
  //         .catch((err) => {
  //           console.log(err);
  //         });
  //     }
  //   } catch (E) {
  //     console.log(E);
  //   }
  // };

  // const nextStep = () => {
  //   setStep(step + 1);
  // };
  const prevStep = () => {
    setStep(step - 1);
  };

  const renderPage = (step) => {
    switch (step) {
      case 1:
        return (
          <View>
            {/* <View style={{ position: "relative" }}>
              {pic === "" ? (
                <Image
                  source={{
                    uri:
                      "https://image.freepik.com/free-vector/businessman-character-avatar-isolated_24877-60111.jpg",
                  }}
                  style={{
                    width: WIDTH,

                    height: HEIGHT / 1.3,
                    borderBottomLeftRadius: WIDTH / 10,
                    borderBottomRightRadius: WIDTH / 10,
                  }}
                />
              ) : (
                <>
                  <ProgressiveImage
                    source={{ uri: pic }}
                    style={{
                      width: WIDTH,
                      height: HEIGHT / 1.3,
                      borderBottomLeftRadius: WIDTH / 10,
                      borderBottomRightRadius: WIDTH / 10,
                    }}
                  />
                </>
              )}
              <View
                style={{
                  flexDirection: "row",
                  position: "absolute",
                  width: WIDTH * 0.9,
                  justifyContent: "space-between",
                  marginHorizontal: 20,
                  height: HEIGHT,
                  marginTop: 20,
                  zIndex: 10,
                }}
              >
                <TouchableOpacity onPress={() => navigation.toggleDrawer()}>
                  <View>
                    <MaterialCommunityIcons
                      name="menu"
                      size={28}
                      color="black"
                      style={{ paddingHorizontal: 20, paddingTop: 10 }}
                    />
                  </View>
                </TouchableOpacity>
                <TouchableOpacity onPress={_pickImage}>
                  <View style={{ alignItems: "center" }}>
                    <Entypo
                      name="upload"
                      size={24}
                      color="black"
                      style={{ paddingHorizontal: 20, paddingTop: 10 }}
                    />

                    <Text style={{ color: "black", fontSize: 14 }}>
                      Change Pic
                    </Text>
                  </View>
                </TouchableOpacity>
              </View>
            </View>
            <View>
