                }}
              >
                {name}
              </Text>
              {age == "" && travellerType == "" ? null : (
                <View>
                  <View
                    style={{
                      flexDirection: "row",
                      alignItems: "center",
                      // paddingBottom: 20,
                    }}
                  >
                    <FontAwesome
                      name="circle"
                      size={20}
                      color="green"
                      style={{
                        marginLeft: WIDTH / 10,
                      }}
                    />
                    <Text
                      style={{
                        marginLeft: WIDTH / 20,
                        color: "black",
                        fontFamily: "Andika",
                      }}
