import React from "react";
import {
  StyleSheet,
  Text,
  TouchableOpacity,
  ScrollView,
  View,
  Image,
  Dimensions,
  Linking,
} from "react-native";
import { AntDesign, FontAwesome } from "@expo/vector-icons";
import ProgressiveImage from "./../../Reusable Components/ProgressiveImage";

const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;
const CountryInnerScreen = ({ navigation, route }) => {
  const item = route.params.item;

  const [visible, setVisible] = React.useState(false);
  const showModal = () => setVisible(true);
  const openWhatsApp = (name) => {
    let url = `whatsapp://send?text=Hi,I would like to go ${name} help me to plan on that &phone= +91 8667801206`;

    Linking.openURL(url).catch(() => {
      alert("Make sure WhatsApp installed on your device");
    });
  };

  return (
    <ScrollView>
      <View style={styles.container}>
        <TouchableOpacity
          style={{
            // height: HEIGHT * 0.8,
            alignSelf: "flex-start",
            width: "10%",
            opacity: 0.6,
            top: Platform.OS === "ios" ? 60 : 20,

            zIndex: 10,
            left: 15,
            position: "absolute",
            alignItems: "center",
          }}
          onPress={() => navigation.goBack()}
        >
          <View>
            <FontAwesome name="arrow-circle-left" size={34} color="black" />
          </View>
        </TouchableOpacity>
        <ProgressiveImage
          style={styles.image}
          source={{ uri: item.imageUrl }}
        />
        <Text style={styles.countryName}>{item.countryName}</Text>

        <TouchableOpacity
          style={{
            zIndex: 1,
            bottom: -40,
            padding: 15,
            position: "absolute",
          }}
          onPress={() =>
            navigation.navigate("CityHome", { name: item.countryName })
          }
        >
          <View
            style={{
              padding: 15,
              alignItems: "center",
              borderRadius: 18,
              backgroundColor: "#E28633",
            }}
          >
            <Text style={styles.button}>Explore Cities</Text>
          </View>
        </TouchableOpacity>
      </View>
      <View
        style={{
          width: WIDTH * 0.9,
          alignItems: "center",
          justifyContent: "center",
          marginHorizontal: 20,
          marginTop: HEIGHT / 15,
        }}
      >
        <Text
          style={{
            padding: 10,
            lineHeight: 18,
            fontSize: 15,
            fontFamily: "Andika",
          }}
        >
          {item.aboutCountry}
        </Text>
      </View>

      <View
        style={{
          justifyContent: "center",
          marginTop: 20,
        }}
      >
        <View
          style={{
            flexDirection: "row",
            marginBottom: 30,
            justifyContent: "space-evenly",
            width: WIDTH * 0.9,
          }}
        >
          <View
            style={{
              justifyContent: "space-evenly",
              alignItems: "center",
              width: WIDTH / 2,
            }}
          >
            <Image
              style={{ height: 60, width: 60, marginTop: 25 }}
              source={{
                uri: "https://firebasestorage.googleapis.com/v0/b/touronapp-248e4.appspot.com/o/Touron%20app%2Fcountry%20Images%2Fvisa.png?alt=media&token=f723f5dc-09a5-40bb-bf87-619fd86aa1dc",
              }}
            />
            <Image
              style={{ height: 60, width: 60, marginTop: 25 }}
              source={{
                uri: "https://firebasestorage.googleapis.com/v0/b/touronapp-248e4.appspot.com/o/Touron%20app%2Fcountry%20Images%2Fweather.png?alt=media&token=430fbd08-4ff2-49fa-8930-b5424d824bce",
              }}
            />
            <Image
              style={{ height: 60, width: 60, marginTop: 25 }}
              source={{
                uri: "https://firebasestorage.googleapis.com/v0/b/touronapp-248e4.appspot.com/o/Touron%20app%2Fcountry%20Images%2Ftime-zone.png?alt=media&token=a5d7b325-b819-46f0-9918-8e8ef3c178f4",
              }}
            />
            <AntDesign
              style={{ marginTop: 18 }}
              name="calendar"
              size={54}
              color="black"
            />
          </View>
          <View
            style={{
              justifyContent: "space-evenly",
              alignItems: "center",
              width: WIDTH / 2,
              marginRight: 20,
            }}
          >
            <View
              style={{
                justifyContent: "space-evenly",
                alignItems: "center",
                width: WIDTH / 2,
                marginTop: 30,
              }}
            >
              <Text
                style={{
                  color: "black",
                  fontSize: 14,
                  fontFamily: "Andika",
                }}
              >
                Eligible for Visa On Arrival
              </Text>
              <Text style={{ fontSize: 20, fontFamily: "NewYorkl" }}>
                {item.visa.onArrival}
              </Text>
            </View>

            <View
              style={{
                alignItems: "center",
                justifyContent: "center",
                marginTop: 30,
              }}
            >
              <Text
                style={{
                  color: "black",
                  fontSize: 14,
                  textAlign: "center",
                  fontFamily: "Andika",
                }}
              >
                Weather throughout the Year
              </Text>
              <Text style={{ fontSize: 20, fontFamily: "NewYorkl" }}>
                {item.weather} ° C
              </Text>
            </View>
            <View
              style={{
                alignItems: "center",
                justifyContent: "center",
                marginTop: 30,
              }}
            >
              <Text
                style={{
                  color: "black",
                  fontSize: 14,
                  textAlign: "center",
                  fontFamily: "Andika",
                }}
              >
                Time Difference From India
              </Text>
              <Text style={{ fontSize: 20, fontFamily: "NewYorkl" }}>
                {item.general.timeZone}
              </Text>
            </View>
            <View
              style={{
                alignItems: "center",
                justifyContent: "center",
                marginTop: 30,
              }}
            >
              <Text
                style={{
                  color: "black",
                  fontSize: 14,
                  textAlign: "center",
                  fontFamily: "Andika",
                }}
              >
                Best Time to Visit
              </Text>
              <Text
                style={{
                  fontSize: 12,
                  fontFamily: "NewYorkl",
                  textAlign: "center",
                }}
              >
                {item.general.bestTimeToVisit.join("/")}
              </Text>
            </View>
          </View>
        </View>

        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-evenly",
            marginBottom: 40,
          }}
        >
          <TouchableOpacity
            onPress={() => {
              showModal();
            }}
          >
            <View
              style={{
                alignItems: "center",
                borderRadius: 13,
                elevation: 200,
                backgroundColor: "#FA3B5A",
                padding: 15,
              }}
            >
              <Image
                style={{ height: 40, width: 40 }}
                source={{
                  uri: "https://firebasestorage.googleapis.com/v0/b/touronapp-248e4.appspot.com/o/Touron%20app%2Fcountry%20Images%2Fshop.png?alt=media&token=a67aec96-88e6-4f78-a686-86a45252d511",
                }}
              />
            </View>
            <Text
              style={{
                textAlign: "center",
                fontFamily: "Andika",
                fontSize: 12,
              }}
            >
              Shopp
            </Text>
          </TouchableOpacity>

          <TouchableOpacity
            onPress={() =>
              navigation.navigate("Planned", {
                countryName: item.countryName,
                type: "International",
              })
            }
          >
            <View
              style={{
                alignItems: "center",
                borderRadius: 13,
                elevation: 200,
                backgroundColor: "#9EB19E",
                padding: 15,
              }}
            >
              <Image
                style={{ height: 40, width: 40 }}
                source={{
                  uri: "https://firebasestorage.googleapis.com/v0/b/touronapp-248e4.appspot.com/o/Touron%20app%2Fcountry%20Images%2Ftravelplan.png?alt=media&token=fb1e0519-63a0-465a-a463-712d284cda0a",
                }}
              />
            </View>
            <Text
              style={{
                fontFamily: "Andika",
                textAlign: "center",
                fontSize: 12,
              }}
            >
              Start Planning for {item.countryName}
            </Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => openWhatsApp(item.countryName)}>
            <View
              style={{
                alignItems: "center",
