import React, { useState, useEffect, useContext } from "react";

import {
  StyleSheet,
  Text,
  View,
  Platform,
  Image,
  FlatList,
  Dimensions,
  TouchableOpacity,
  StatusBar,
  ActivityIndicator,
} from "react-native";

import { Feather, AntDesign } from "@expo/vector-icons";
import touron from "../../api/touron";
import { LinearGradient } from "expo-linear-gradient";
const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;

import { SelfTourContext } from "../../context/ SelfTourContext";
import axios from "axios";

const SelfTourHome = ({
  navigation,
  prevStep,
  selectedCitys,
  setStep,
  selectedCityNamess,
}) => {
  const { setDetails, details } = useContext(SelfTourContext);
  const [tour, setTour] = useState([]);
  const [error, setErrorMessage] = useState();
  const [loader, setLoader] = useState(true);
  const selectedCity = selectedCitys;
  const selectedCityNames = selectedCityNamess;
  const [active, setActive] = useState(0);
  const cityLength = selectedCity.length - 1;
  const [selectedTours, setSelectedTours] = useState([]);
  const [selectedTourNames, setSelectedTourNames] = useState([]);
  const getTour = async (city) => {
    try {
      const tourResponse = await touron.get(`/tour/cityname/${city}`);
      setTour(tourResponse.data);
      setLoader(false);
    } catch (err) {
      console.log(err, "err");
      setErrorMessage("Something went wrong");
    }
  };

  useEffect(() => {
    const source = axios.CancelToken.source();
    getTour(selectedCityNames[active]);
    return () => source.cancel();
  }, []);

  return (
    <View style={styles.container}>
      <StatusBar />
      <View
        style={{
          width: WIDTH * 0.9,
          alignItems: "flex-end",
          justifyContent: "center",
          flexDirection: "row",
          justifyContent: "space-between",
          marginHorizontal: 30,
          position: "relative",
          paddingVertical: 30,
          marginTop: Platform.OS === "ios" ? 20 : 0,
        }}
      >
        <TouchableOpacity
          onPress={() => {
            prevStep();
          }}
        >
          <View>
            <AntDesign name="arrowleft" size={28} />
          </View>
        </TouchableOpacity>

        <Text
          style={{
            fontSize: 20,
            fontFamily: "NewYorkl",
            marginBottom: Platform.OS === "ios" ? 15 : 0,

            // marginBottom: Platform.OS === "ios" ? 20 : 0,
          }}
        >
          Select Tours for {selectedCityNames[active]}
        </Text>

        <TouchableOpacity>
          <View>{/* <AntDesign name="arrowright" size={28} /> */}</View>
        </TouchableOpacity>
      </View>
      <View style={(styles.view, { shadowOpacity: 1 })}></View>

      <View>
        {loader ? (
          <ActivityIndicator
            size="large"
            style={{
              flex: 1,
              alignItems: "center",
              justifyContent: "center",
            }}
          />
        ) : (
          <View style={{ paddingBottom: 100, marginBottom: 40 }}>
            {tour.length == 0 ? (
              <View
                style={{
                  alignItems: "center",
                  justifyContent: "center",
                }}
              >
                <View>
                  <Image
                    style={{ width: WIDTH * 0.9, height: WIDTH * 0.9 }}
                    source={require("../../../assets/oops.jpg")}
                  />
                </View>
                <Text style={{ fontFamily: "Avenir", fontSize: 23 }}>
                  No Tours Found
                </Text>
              </View>
            ) : (
              <FlatList
                showsVerticalScrollIndicator={false}
                keyExtractor={(c) => c._id}
                data={tour}
                style={{ paddingBottom: 20, flex: 1, height: HEIGHT * 0.8 }}
                renderItem={({ item }) => {
                  return (
                    <View>
                      {!selectedTourNames.includes(item.tourName) ? null : (
                        <TouchableOpacity style={styles.tickImageContainer}>
                          <Feather
                            name="check-circle"
                            size={34}
                            color="green"
                            style={{
                              bottom: Platform.OS === "ios" ? -5 : 16,
                              right: 10,
                              zIndex: 10,
                              position: "absolute",
                            }}
                          />
                        </TouchableOpacity>
                      )}

                      <View style={styles.imageContainer}>
                        <View style={styles.shadow}>
                          <TouchableOpacity
                            onPress={() => {
                              if (selectedTourNames.includes(item.tourName)) {
                                let tours = selectedTourNames.filter((c) => {
                                  return c !== item.tourName;
                                });
                                setSelectedTourNames(tours);

                                let updatedTours = selectedTours.filter((c) => {
                                  return c.tourName !== item.tourName;
                                });
                                setSelectedTours(updatedTours);
                              } else {
                                setSelectedTourNames([
                                  ...selectedTourNames,
                                  item.tourName,
                                ]);
                                setSelectedTours([...selectedTours, item]);
                              }
                            }}
                          >
                            <Image
                              style={styles.image}
                              source={{ uri: item.imageUrl }}
                            />
                          </TouchableOpacity>
                          <View style={styles.cityNameContainer}>
                            <LinearGradient
                              colors={["#FFA26E", "#E36D5D"]}
                              style={styles.gradient}
                            >
                              <View>
                                <Text style={styles.cityname}>
                                  {item.cityName}
                                </Text>
                              </View>
                            </LinearGradient>
                            <View>
                              <Feather
                                name="bookmark"
                                color="#fff"
                                style={styles.iconStyle}
                              ></Feather>
                            </View>
                          </View>
                          <View style={styles.tourDetails}>
                            <Text style={styles.tourFeatures}>
                              {item.tourCategory.join(", ")}
                            </Text>
                            <Text
                              style={{
                                fontSize: 18,
                                fontFamily: "Avenir",
                              }}
                            >
                              {item.tourName}
                            </Text>
                            <Text style={styles.tourFeatures}>
                              {item.tourType}
                            </Text>
                            <View style={styles.star}>
                              <View style={styles.costContainer}>
                                {item.tourCost.adult == 15000 &&
                                item.tourCost.adult >= 10000 ? (
                                  <Text style={styles.cost}>₹₹₹₹ - High</Text>
                                ) : item.tourCost.adult < 10000 &&
                                  item.tourCost.adult >= 5000 ? (
                                  <Text style={styles.cost}>₹₹₹ - Medium</Text>
                                ) : item.tourCost.adult > 2500 &&
                                  item.tourCost.adult < 500205 ? (
                                  <Text style={styles.cost}>₹₹ - Low</Text>
                                ) : (
                                  <Text style={styles.cost}>₹ - Very Low</Text>
                                )}
                              </View>
                              <View
                                style={[
                                  styles.costContainer,
                                  {
                                    paddingVertical: 3,
                                    paddingHorizontal: 5,
                                  },
                                ]}
                              >
                                <TouchableOpacity
                                  onPress={() => {
                                    navigation.navigate("SelfTourInner", {
                                      item: item,
                                    });
                                  }}
                                >
                                  <Text
                                    style={{
                                      fontSize: 13,
                                      fontFamily: "Andika",
                                    }}
                                  >
                                    See More
                                  </Text>
                                </TouchableOpacity>
                              </View>

                              <View style={styles.star}>
                                <Image
                                  style={{
                                    height: 25,
                                    width: 25,
                                    marginRight: 4,
                                  }}
                                  source={require("../../../assets/Star.png")}
                                />
                                <Text style={{ fontSize: 18 }}>
                                  {item.ratings}/5
                                </Text>
                              </View>
                            </View>
                          </View>
                        </View>
                      </View>
                    </View>
                  );
                }}
              />
            )}
          </View>
        )}
      </View>
      <View
        style={{
          position: "absolute",
          bottom: 0,
          width: WIDTH * 0.95,
          flexDirection: "row",
          alignItems: "center",
          justifyContent: "center",
          backgroundColor: "transparent",
        }}
      >
        {active == 0 ? null : (
          <TouchableOpacity
            style={{ flex: 0.5 }}
            onPress={() => {
              const cityLength = selectedCityNames.length - 1;

              //  console.log(active);
              if (active <= cityLength) {
                getTour(selectedCityNames[active - 1]);
                setActive(active - 1);
              }
              setLoader(true);
            }}
          >
            <View style={[styles.button, { marginRight: 1 }]}>
              <Text style={styles.buttonText}>Back</Text>
            </View>
          </TouchableOpacity>
        )}
        {cityLength == active ? null : (
          <TouchableOpacity
            style={{ flex: 1.5 }}
            onPress={() => {
              const cityLength = selectedCityNames.length - 1;

              //   console.log(active);
              if (active <= cityLength) {
                setActive(active + 1);
                getTour(selectedCityNames[active + 1]);
              }
              setLoader(true);
            }}
          >
            <View style={styles.button}>
              <Text style={styles.buttonText}>
                Proceed to {selectedCityNames[active + 1]}
              </Text>
            </View>
          </TouchableOpacity>
        )}
        {cityLength == active ? (
          <TouchableOpacity
            style={{ flex: 1.5 }}
            onPress={() => {
              setDetails({
                ...details,
                selectedTours: selectedTours,
              });
              setStep();
            }}
          >
            <View style={styles.button}>
              <Text style={styles.buttonText}>Next</Text>
            </View>
          </TouchableOpacity>
        ) : null}
      </View>
      {/* )} */}
    </View>
  );
};

