import React, { useState, useEffect } from "react";
import {
  Text,
  View,
  StatusBar,
  Image,
  TouchableOpacity,
  Platform,
  ActivityIndicator,
  StyleSheet,
  Dimensions,
  ScrollView,
} from "react-native";
import moment from "moment";
import { FontAwesome } from "@expo/vector-icons";
import touron from "../../api/touron";
import HTMLView from "react-native-htmlview";
const WIDTH = Dimensions.get("window").width;
const HEIGHT = Dimensions.get("window").height;
import axios from "axios";

const BlogInnerScreen = ({ navigation, route }) => {
  const { title, id } = route.params;
  const [blogDetails, setBlogDetails] = useState({});
  const [loaded, setLoaded] = useState(false);

  const getBlogDetails = async () => {
    try {
      setLoaded(true);
      const blogResponse = await touron.get(`/blog/edit/${id}`);
      setBlogDetails(blogResponse.data);
      setLoaded(false);
    } catch (err) {
      console.log(err, "ergggr");
    }
  };

  useEffect(() => {
    const source = axios.CancelToken.source();
    getBlogDetails();

    return () => source.cancel();
  }, [id]);

  return (
    <>
      {loaded ? (
        <View
          style={{ flex: 1, alignItems: "center", justifyContent: "center" }}
        >
          <View
            style={{
              position: "absolute",
              zIndex: 2,
              alignItems: "center",
              flexDirection: "row",
              top: 0,
              left: 0,
              marginTop: Platform.OS === "ios" ? 55 : 25,
            }}
          >
            <TouchableOpacity
              onPress={() => {
                navigation.navigate("BlogHome");
              }}
            >
              <View
                style={{
                  paddingLeft: 20,
                }}
              >
                <FontAwesome name="arrow-circle-left" size={34} color="black" />
              </View>
            </TouchableOpacity>
          </View>
          <ActivityIndicator size="large" />
        </View>
      ) : (
        <ScrollView style={{ flex: 1, backgroundColor: "#FFF" }}>
          <StatusBar />
          <View
            style={{
              // height: HEIGHT / 10,
              position: "absolute",
              zIndex: 2,
              alignItems: "center",
              flexDirection: "row",
              marginTop: Platform.OS === "ios" ? 55 : 25,
            }}
          >
            <TouchableOpacity
              onPress={() => {
                navigation.navigate("BlogHome");
              }}
            >
              <View
                style={{
                  paddingLeft: 20,
                }}
              >
                <FontAwesome name="arrow-circle-left" size={34} color="black" />
              </View>
            </TouchableOpacity>
          </View>
          <View style={{ marginBottom: 25 }}>
            <View style={{ position: "relative" }}>
              <Image
                style={{
                  width: WIDTH,
                  height: HEIGHT / 2,
                  borderBottomRightRadius: 30,
                  borderBottomLeftRadius: 30,
                }}
                source={{ uri: blogDetails.imageSrc }}
              />
              {Object.keys(blogDetails).includes("imageCredit") &&
                blogDetails.imageCredit !== "" && (
                  <View
                    style={[
                      styles.credit,
                      {
                        borderBottomRightRadius: 30,
                        borderBottomLeftRadius: 30,
                      },
                    ]}
                  >
                    <Text style={styles.creditText}>
                      Credits : {blogDetails.imageCredit}
                    </Text>
                  </View>
                )}
            </View>
            {Object.keys(blogDetails).includes("countryName") && (
              <View
                style={{
                  flexDirection: "row",
                  alignItems: "center",
                  paddingLeft: 10,
                }}
              >
                <Text
                  style={{
                    backgroundColor: "transparent",
                    borderColor: "black",
                    padding: 8,
                    margin: 8,
                    fontSize: 12,
                    fontFamily:
                      Platform.OS === "ios" ? "AvenirNext-Bold" : "Avenir",
                    borderWidth: 1,
                    borderRadius: 10,
                  }}
                >
                  {blogDetails.countryName}
                </Text>
              </View>
            )}

            <Text
              style={{
                fontFamily:
                  Platform.OS === "ios" ? "AvenirNext-Bold" : "Avenir",

                fontSize: 26,
                margin: 10,
              }}
            >
              {blogDetails.blogTitle}
            </Text>
            <View style={{ flexDirection: "row", alignItems: "center" }}>
              <View
                style={{
                  marginHorizontal: 10,
                }}
              >
                <Image
                  style={{
                    width: WIDTH / 7,
                    height: WIDTH / 7,
                    borderRadius: 50,
                  }}
                  source={require("../../../assets/logo.jpeg")}
                />
              </View>
              <Text style={{ fontFamily: "Andika", fontSize: 16 }}>
                {moment(blogDetails.createdAt).format("MMMM Do YYYY")}
                {"\n"}
                by{" "}
                {!Object.keys(blogDetails).includes("writtenBy") ||
                blogDetails.writtenBy === ""
                  ? "tour  On team"
                  : blogDetails.writtenBy}
              </Text>
            </View>
            {Object.keys(blogDetails).includes("content") &&
            blogDetails.content.charAt(0) === "<" ? (
              <HTMLView
                value={blogDetails.content}
                stylesheet={{
                  p: {
                    marginVertical: 10,
                    marginHorizontal: 15,
                    fontSize: 15,
                    fontFamily: "Andika",
                    lineHeight: HEIGHT / 40,
                  },
                  h1: {
                    fontFamily: "Andika",
                    color: "#fff",
                  },
                }}
              />
            ) : (
              <Text
                style={{
                  marginVertical: 10,
                  marginHorizontal: 15,
                  fontFamily: "Andika",
                  fontSize: 15,
                  lineHeight: HEIGHT / 40,
                }}
              >
                {blogDetails.content}
              </Text>
            )}

            {blogDetails.subHeading1 == "" ? null : (
              <View style={{ margin: 10 }}>
                <Text
                  style={{
                    fontFamily:
                      Platform.OS === "ios" ? "AvenirNext-Bold" : "Avenir",
                    fontSize: 18,
                  }}
                >
                  {blogDetails.subHeading1}
                </Text>
              </View>
            )}
            {Object.keys(blogDetails).includes("imageSrc1") &&
            blogDetails.imageSrc1.length < 10 ? null : (
              <View style={{ position: "relative" }}>
                <Image
                  style={{ width: WIDTH, height: HEIGHT / 3 }}
                  source={{ uri: blogDetails.imageSrc1 }}
                />

                {Object.keys(blogDetails).includes("imageCredit1") &&
                  blogDetails.imageCredit1 !== "" && (
                    <View style={styles.credit}>
                      <Text style={styles.creditText}>
                        Credits : {blogDetails.imageCredit1}
                      </Text>
                    </View>
                  )}
              </View>
            )}
            {blogDetails.content1 == "" ? null : (
              <>
                {Object.keys(blogDetails).includes("content1") &&
                blogDetails.content1.charAt(0) === "<" ? (
                  <HTMLView
                    value={blogDetails.content1}
                    stylesheet={{
                      p: {
                        marginVertical: 10,
                        marginHorizontal: 15,
                        fontSize: 15,
                        fontFamily: "Andika",
                        lineHeight: HEIGHT / 40,
                      },
                      h1: {
                        fontFamily: "Andika",
                        color: "#fff",
