              <View style={{ position: "relative" }}>
                <Image
                  style={{ width: WIDTH, height: HEIGHT / 3 }}
                  source={{ uri: blogDetails.imageSrc2 }}
                />
                {Object.keys(blogDetails).includes("imageCredit2") &&
