              </View>
            )}
            {blogDetails.content2 == "" ? null : (
              <>
                {Object.keys(blogDetails).includes("content2") &&
                blogDetails.content2.charAt(0) === "<" ? (
                  <HTMLView
                    value={blogDetails.content2}
                    stylesheet={{
                      p: {
                        marginVertical: 10,
                        marginHorizontal: 15,
                        fontSize: 15,
                        fontFamily: "Andika",
                        lineHeight: HEIGHT / 40,
                      },
                      h1: {
                        fontFamily: "Andika",
                        color: "#fff",
                      },
                    }}
                  />
                ) : (
                  <Text
                    style={{
                      marginVertical: 10,
                      marginHorizontal: 15,
                      fontFamily: "Andika",
                      fontSize: 15,
                      lineHeight: HEIGHT / 40,
                    }}
                  >
                    {blogDetails.content2}
                  </Text>
                )}
              </>
            )}

            {blogDetails.subHeading3 == "" ? null : (
              <View style={{ margin: 10 }}>
                <Text
                  style={{
                    fontFamily:
                      Platform.OS === "ios" ? "AvenirNext-Bold" : "Avenir",
                    fontSize: 18,
                  }}
                >
                  {blogDetails.subHeading3}
                </Text>
              </View>
            )}
            {Object.keys(blogDetails).includes("imageSrc3") &&
            blogDetails.imageSrc3.length < 10 ? null : (
              <View style={{ position: "relative" }}>
                <Image
                  style={{ width: WIDTH, height: HEIGHT / 3 }}
                  source={{ uri: blogDetails.imageSrc3 }}
                />
                {Object.keys(blogDetails).includes("imageCredit3") &&
                  blogDetails.imageCredit3 !== "" && (
                    <View style={styles.credit}>
                      <Text style={styles.creditText}>
                        Credits : {blogDetails.imageCredit3}
                      </Text>
                    </View>
                  )}
              </View>
            )}
            {blogDetails.content3 == "" ? null : (
              <>
                {Object.keys(blogDetails).includes("content3") &&
                blogDetails.content3.charAt(0) === "<" ? (
                  <HTMLView
                    value={blogDetails.content3}
                    stylesheet={{
                      p: {
                        marginVertical: 10,
                        marginHorizontal: 15,
                        fontSize: 15,
                        fontFamily: "Andika",
                        lineHeight: HEIGHT / 40,
                      },
                      h1: {
                        fontFamily: "Andika",
                        color: "#fff",
                      },
                    }}
                  />
                ) : (
                  <Text
                    style={{
                      marginVertical: 10,
                      marginHorizontal: 15,
                      fontFamily: "Andika",
                      fontSize: 15,
                      lineHeight: HEIGHT / 40,
                    }}
                  >
                    {blogDetails.content3}
                  </Text>
                )}
              </>
            )}
          </View>
        </ScrollView>
      )}
    </>
  );
};

